import React, { Component } from 'react';
import CloseIcon from '@material-ui/icons/Close';
import ConfirmationPopup from './ConfirmationPopup';
class Popup extends React.Component {
  constructor() {
    super();
    this.state = {
      showConfirmationPopup: false,
      showPopup: false,

    };
  }
  toggleConfirmationPopup() {
    this.setState({
      showConfirmationPopup: !this.state.showConfirmationPopup,
      showPopup: !this.state.showPopup

    });
  }
    render() {
      return ( <div>{this.state.showPopup ?
        <div className='popup'>
          <div className='popup_inner'>
          <button id="close"onClick={this.props.closePopup}><CloseIcon></CloseIcon>  </button>

          <div id="universities" onClick={this.toggleConfirmationPopup.bind(this)} >Technical University of Munich </div>
          <div id="universities" onClick={this.toggleConfirmationPopup.bind(this)} >Ludwig Maximilian University of Munich</div>
          <div id="universities"onClick={this.toggleConfirmationPopup.bind(this)} > ETH Zürich  </div>
          <div id="universities" onClick={this.toggleConfirmationPopup.bind(this)} > University of Twente</div>
          <div> {this.state.showConfirmationPopup ? 
          <ConfirmationPopup
            text='Close Me'
            closePopup={this.toggleConfirmationPopup.bind(this)}
          />
          : null
        }
        </div>



          </div>
        </div>  :null }</div>
      );
    }
  }
  export default Popup;
