import React, { Component } from 'react';
import CloseIcon from '@material-ui/icons/Close';
import ConfirmationPopup from './ConfirmationPopup';
class Popup extends React.Component {
  constructor() {
    super();
    this.state = {
      showPopup: false
    };
  }
  togglePopup() {
    this.setState({
      showPopup: !this.state.showPopup
    });
  }
    render() {
      return (
        <div className='popup'>
          <div className='popup_inner'>
          <button id="close"onClick={this.props.closePopup}><CloseIcon></CloseIcon>  </button>

          <div id="universities" onClick={this.togglePopup.bind(this)} >Technical University of Munich </div>
          <div id="universities" onClick={this.togglePopup.bind(this)} >Ludwig Maximilian University of Munich</div>
          <div id="universities"onClick={this.togglePopup.bind(this)} > ETH Zürich  </div>
          <div id="universities" onClick={this.togglePopup.bind(this)} > University of Twente</div>
          <div> {this.state.showPopup ? 
          <ConfirmationPopup
            text='Close Me'
            closePopup={this.togglePopup.bind(this)}
          />
          : null
        }
        </div>



          </div>
        </div>
      );
    }
  }
  export default Popup;
