import React, { Component } from "react";
import CloseIcon from "@material-ui/icons/Close";
import ConfirmationPopup from "./ConfirmationPopup";
import axios from 'axios';
import AssuredWorkloadIcon from '@mui/icons-material/AssuredWorkload';
import Avatar from "@mui/material/Avatar";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";

class Popup extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showConfirmationPopup: false,
            showPopup: true,
        };
    }
    toggleConfirmationPopup() {
        this.setState({
            showConfirmationPopup: !this.state.showConfirmationPopup,
        });
    }
    togglePopup() {
        this.setState({
            showPopup: !this.state.showPopup,
        });
    }
    async toggleBoth() {
        this.setState({
            showConfirmationPopup: !this.state.showConfirmationPopup,
            showPopup: !this.state.showPopup,
        });
        await axios.post('http://localhost:4003/transcript/create',JSON.parse(this.props.fileInfo) ).then((response) => {
            console.log(response);
            console.log(response.data);
          });

    }
    render() {
        if (this.state.showPopup) {
            return (
                <div className="popup">
                    <div className="popup_inner">
                        <button id="close" onClick={this.props.closePopup}>
                            <CloseIcon></CloseIcon>{" "}
                        </button>
                        <List sx={{ width: "100%", maxWidth: 360, bgcolor: "background.paper" }}>
      <ListItem>
        <ListItemAvatar>
          <Avatar>
            <AssuredWorkloadIcon />
          </Avatar>
        </ListItemAvatar>
        <ListItemText id="Avater1"primary="Technical university in munich" secondary="test" />
      </ListItem>
      <ListItem>
        <ListItemAvatar>
          <Avatar>
            <AssuredWorkloadIcon />
          </Avatar>
        </ListItemAvatar>
        <ListItemText primary="Work" secondary="Jan 7, 2014" />
      </ListItem>
      <ListItem>
        <ListItemAvatar>
          <Avatar>
            <AssuredWorkloadIcon />
          </Avatar>
        </ListItemAvatar>
        <ListItemText primary="Vacation" secondary="July 20, 2014" />
      </ListItem>
    </List>
                        <h3
                            id="universities"
                            onClick={this.toggleBoth.bind(this)}
                        > 
                            Technical University of Munich{" "} 
                        </h3>
                        <h3
                            id="universities"
                            onClick={this.toggleBoth.bind(this)}
                        >
                            Ludwig Maximilian University of Munich
                        </h3>
                        <h3
                            id="universities"
                            onClick={this.toggleBoth.bind(this)}
                        >
                            {" "}
                            ETH Zürich{" "}
                        </h3>
                        <h3
                            id="universities"
                            onClick={this.toggleBoth.bind(this)}
                        >
                            {" "}
                            University of Twente
                        </h3>

                        <div>
                            {" "}
                            {this.state.showConfirmationPopup ? (
                                <ConfirmationPopup
                                    text="Close Me"
                                    closePopup={this.toggleConfirmationPopup.bind(
                                        this
                                    )}
                                />
                            ) : null}
                        </div>
                    </div>
                </div>
            );
        } else
            return (
                <div>
                    {" "}
                    {this.state.showConfirmationPopup ? (
                        <ConfirmationPopup
                            text="Close Me"
                            closePopup={this.toggleConfirmationPopup.bind(this)}
                        />
                    ) : null}
                </div>
            );
    }
}
export default Popup;
