import "../App.css";
import Grid from "@material-ui/core/Grid";
import { Button } from "@mui/material";
import React, { Component } from "react";
import Popup from "../component/Popup.js";
import AppNavBar from "../component/AppNavBar";
import axios from 'axios';
import SendIcon from '@mui/icons-material/Send';
import { DropzoneAreaBase } from 'material-ui-dropzone';



class Upload extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showPopup: false,
            showSendToUniversity: false,
            uploadfile:{},
            testBackend:"",
            buttonDisable:true,
        };
        this.handleselectedFileDropzone=this.handleselectedFileDropzone.bind(this);
    }
    togglePopup() {

        this.setState({
            showPopup: !this.state.showPopup,
        });
    }

    handleselectedFileDropzone= async (event) => {
        console.log(event[0].file);
        var file =event[0].file;
        var reader = new FileReader();
        reader.onload = async (event)=>{
            console.log(event.target.result);
            this.setState({
                uploadfile:event.target.result, 
            })
            await axios.post('http://localhost:4003/transcript/create',JSON.parse(this.state.uploadfile) ).then((response) => {
                console.log(response);
                console.log(response.data);
              }); 
         }
         reader.readAsText(file);
         this.setState({
            selectedFile: event[0].file,
            selectedFileName: event[0].file.name,
            showSendToUniversity: true,
            buttonDisable:false,

        });

   } 

    render() {
        return (
            <container  >
                <AppNavBar />
                <p>{}</p>
                <h1 id="title2">exChainge</h1>
                <div id="subTitle2">Upload your transcripts and send it to the exchange university easily and safely </div>
                <div >
                <Grid id="iconsPosition" container>
                    <Grid xs={1}> </Grid>

                    <div>
                        {" "}
                      { /* {this.state.showSendToUniversity ? ( */}
                            <Button
                            endIcon={<SendIcon />}
                            style={{
                                backgroundColor: "rgb(53, 110, 255)",
                                width: '40em',
                            }}
                                variant="contained"
                                disabled={this.state.buttonDisable}
                                onClick={this.togglePopup.bind(this)}
                                id="SendToUniversity"
                            >
                                Send to University{" "}
                            </Button>
                       {/* ) : null} */}
                    </div>

                    <div>
                        {" "}
                        {this.state.showPopup ? (
                            <Popup
                                text="Close Me"
                                closePopup={this.togglePopup.bind(this)}
                                fileInfo={this.state.uploadfile}
                            />
                        ) : null}
                    </div>
                    <h3 id="uploadFileName">{this.state.selectedFileName} </h3>
                  <div id="dropDown2"> <DropzoneAreaBase onAdd={this.handleselectedFileDropzone}> </DropzoneAreaBase></div> 




                </Grid>
                </div>
            </container>
        );
    }
}
export default Upload;
