import UploadFileIcon from "@mui/icons-material/UploadFile";
import "../App.css";
import Grid from "@material-ui/core/Grid";
import { Button } from "@mui/material";
import React, { Component } from "react";
import AppNavBar from "../component/AppNavBar";

import axios from 'axios';
import SendIcon from '@mui/icons-material/Send';
import { DropzoneAreaBase } from 'material-ui-dropzone';
import SelectionList from "../component/SelectionList";
import { readPartnerUni } from "../services/api";
import Snackbar from '@mui/material/Snackbar';
import { sendTranscript } from "../services/blockchain/contractCalls";
import { getCurrentAccount, startService } from "../services/blockchain/util";
import hash from "object-hash";

function Upload(props) {
    const [selectedUniIndex, setSelectedUniIndex] = React.useState(-1);

    const [showToast, setShowToast] = React.useState(false);
    const [uploadedFile, setUploadedFile] = React.useState(null);
    const [selectedFile, setSelectedFile] = React.useState({});

    // const testList = [
    //     { name: "x", serverAddress: "http://localhost:4004/transcript/create", blockchainAddress: "x" },
    //     { name: "y", serverAddress: "http://localhost:4004/transcript/create", blockchainAddress: "y" },
    // ];
    const [receiverList, setReceiverList] = React.useState([]);

    React.useEffect(() => {
        //console.log("Partner uni data:",localStorage.getItem('partnerUnis'))
        setReceiverList(JSON.parse(localStorage.getItem('partnerUnis')));
    }, []);

    const handleselectedFileDropzone = async (event) => {
        console.log(event[0].file);
        var file = event[0].file;
        var reader = new FileReader();
        reader.onload = async (event) => {
            console.log(event.target.result);
            setUploadedFile(event.target.result);
        };
        reader.readAsText(file);
        setSelectedFile(event[0].file);
    };

    const storeTranscript = async (isBatched) => {
        // TODO store differently if batched
        const receiverUni = receiverList[selectedUniIndex];
        // await axios.post('http://localhost:4003/transcript/create', JSON.parse(uploadedFile)).then((response) => {
        //     console.log(response);
        //     console.log(response.data);
        // });
    };

    const handleSendLater = async () => {
        await storeTranscript(true);
        setShowToast(true);
    };

    const handleSendNow = async () => {
        const receiverUni = receiverList[selectedUniIndex];
        await storeTranscript(false);

        await startService();
        // const dummyReceiver = getCurrentAccount();
        const transcriptHash = hash(uploadedFile);

        try {
            const index = await sendTranscript(transcriptHash, receiverUni.blockchain_address);
            // await axios.post(receiverUni.server_address, JSON.parse(uploadedFile)).then((response) => {
            //     console.log(response);
            //     console.log(response.data);
            // });
            setShowToast(true);
        } catch (e) {
            console.log(e);
        }
    };

    return (
        <container>
            <AppNavBar />
            <h1 id="title2">exChainge</h1>
            <div id="subTitle2">Upload your transcripts and send it to the exchange university easily and safely </div>

            <div id="dropDown2">
                <DropzoneAreaBase onAdd={handleselectedFileDropzone} />
                <SelectionList options={receiverList} selection={selectedUniIndex} setSelection={setSelectedUniIndex} />

                <p></p>
                {uploadedFile !== null && (
                    <>
                        <h3>Uploaded file: {selectedFile.name}</h3>
                        <SelectionList options={receiverList} selection={selectedUniIndex} setSelection={setSelectedUniIndex} />
                    </>
                )}
                <p></p>
                {selectedUniIndex !== -1 &&
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                        <Button onClick={handleSendLater} style={{ backgroundColor: "rgb(53, 110, 255)" }} variant="contained">Send Later</Button>
                        <Button onClick={handleSendNow} style={{ backgroundColor: "rgb(53, 110, 255)" }} variant="contained">Send Now</Button>
                    </div>
                }
            </div>

            <Snackbar
                open={showToast}
                autoHideDuration={6000}
                onClose={() => setShowToast(false)}
                message="Transcript sent"
            />
        </container>
    );
}
export default Upload;
