import React from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { PropTypes } from 'prop-types';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import backgroundImage from '../img/blockchain2.jpeg';
import Popup from 'reactjs-popup';
import LoginView from './LoginView';


const useStyles = makeStyles((theme) => ({
  root: {
    height: '100vh',
    backgroundImage: `url(${backgroundImage})`,
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    filter: 'brightness(80%)',
  },
  text: {
    textAlign: 'center',
    color: '#fff',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
  },
  buttons: {
    '& > *': {
      margin: theme.spacing(1),
    },
  },
  buttonStyle1: {
    background: '#1d9419',
    color: '#fff',
  },
  buttonStyle2: {
    background: '#fde2e2',
    color: '#000',
  },
}));

const Landing = () => {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Box p={20}>
        <Box id="LandingTitle"
          className={classes.text}
          fontSize='h2.fontSize'
          fontWeight='fontWeightBold'
          fontFamily='Monospace'

        >
          Welcome to Easy Exchange 
        </Box>
        <Box
          className={classes.text}
          fontSize='h5.fontSize'
          fontStyle='italic'
          fontWeight='fontWeightLight'
        >
          Verify Transcripts with Blockchain Technology 
        </Box>
        <Box className={classes.buttons} textAlign='center' m={3}>
          <Button
            className={classes.buttonStyle1}
            component={Link}
            to='/signup'
            variant='contained'
          >
            Sign Up
          </Button>

          <Popup trigger={<button variant='contained'>
            Login
          </button>} >
    <div> <LoginView></LoginView> </div>
  </Popup>
        </Box>
      </Box>
    </div>
  );
};



export default Landing;
