import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Paper, Button, TextField, Typography } from "@material-ui/core";
import AlertDialog from "../modal/Dialog";
import { useSelector } from "react-redux";
import validator from 'validator'
import { isValidElement } from "react";

const useStyles = makeStyles((theme) => ({
    userLoginRoot: {
        margin: "auto",
    },
    loginPaper: {
        width: "400px",
        padding: theme.spacing(2),
    },
    loginRow: {
        paddingTop: theme.spacing(1),
        paddingBottom: theme.spacing(1),
        "&:last-child": {
            paddingBottom: theme.spacing(1),
        },
        "&:first-child": {
            paddingTop: theme.spacing(0),
        },
    },
    loginButtons: {
        display: "flex",
        justifyContent: "space-between",
    },
    loginButton: {
        marginLeft: theme.spacing(1),
    },
}));

/**
 * For user login
 * @param {props} props
 */
function SignupComponent(props) {
    const classes = useStyles();

    const user = useSelector((state) => state.user);
    const [errorMessage, setErrorMessage] = React.useState('');
    const validate = (value) => {
        if (validator.isURL(value)) 
          return true;
        else {
          setErrorMessage('Please enter a valid server address');
          return false;
        }
      }

    const [username, setUsername] = React.useState("");
    const [serverAddress, setAddress] = React.useState("");
    const [openDialog, setOpenDialog] = React.useState(false);
    const [loginError, setLoginError] = React.useState("");

    useEffect(() => {
        if (user?.error) {
            setErrorMessage("User not created!")
            setOpenDialog(true);
        } else {
        }
    }, [user]);

    const onSignup = (e) => {
        e.preventDefault();
        if(validate(serverAddress))
            props.onSignup(username,serverAddress);
        else
        setOpenDialog(true);
    };

    const onChangeUsername = (e) => {
        setUsername(e.target.value);
        setLoginError("");
    };
    const onChangeAddress = (e) => {
        setAddress(e.target.value);
        setLoginError("");
    };

    /*const onChangePassword = (e) => {
        setPassword(e.target.value);
        setLoginError("");
    };*/

    return (
        <div className={classes.userLoginRoot}>
            <Paper className={classes.loginPaper} component="form">
                <br />
                <center>
                    <h4>Welcome to SEBA - Student Mobility!</h4>
                </center>
                <br />
                <div className={classes.loginRow}>
                    <TextField
                        label="Username"
                        fullWidth
                        value={username}
                        onChange={onChangeUsername}
                        error={loginError !== ""}
                    />
                </div>
                <div className={classes.loginRow}>
                    <TextField
                        label="Address"
                        fullWidth
                        value={serverAddress}
                        onChange={onChangeAddress}
                        error={loginError !== ""}
                    />
                </div>
                {loginError !== "" ? (
                    <div className={classes.loginRow}>
                        <Typography color="error">{loginError}</Typography>
                    </div>
                ) : null}
                <div className={classes.loginRow + " " + classes.loginButtons}>
                    <div>
                        <Button
                            className={classes.loginButton}
                            onClick={props.onCancel}
                        >
                            Cancel
                        </Button>
                        <Button
                            className={classes.loginButton}
                            variant="contained"
                            color="primary"
                            onClick={onSignup}
                            disabled={username === ""}
                            type="submit"
                        >
                            Signup
                        </Button>
                    </div>
                </div>
            </Paper>
            <AlertDialog
                open={openDialog}
                onClose={() => setOpenDialog(false)}
                secondButton={false}
                title={errorMessage}
            />
        </div>
    );
}

export default SignupComponent;
