import React, {useEffect} from "react";
import {withRouter} from "react-router-dom";
import {connect, Provider, useSelector} from "react-redux";
import {Container, Col} from "react-bootstrap";
import {ThemeProvider} from "@material-ui/styles";
import {signup} from "../redux/action/userAction";
import SignupComponent from "../component/SignupComponent";
import {Theme} from "../component/UI/Theme";
import Web3 from 'web3';
import  {readNounce} from '../api'

/**
 * For user login
 * @param {props} props
 */

function SignupView(props) {
    const userData = useSelector((state) => state.user);
    let web3 = undefined;
    useEffect(() => {
        if (userData?.user?.username) {
            props.history.push("/login");
        }
    }, [userData, props.history]);

    const onSignUp = async (username) => {
        if (!(window).ethereum) {
			window.alert('Please install MetaMask first.');
			return;
		}


		if (!web3) {
			try {
				// Request account access if needed
				await (window).ethereum.enable();

				// We don't know window.web3 version, so we use our own instance of Web3
				// with the injected provider given by MetaMask
				web3 = new Web3((window).ethereum);
			} catch (error) {
				window.alert('You need to allow MetaMask.');
				return;
			}
		}

        const coinbase = await web3.eth.getCoinbase();
		if (!coinbase) {
			window.alert('Please activate MetaMask first.');
			return;
		}

        const publicAddress = coinbase.toLowerCase();
        props.dispatch(signup(username, publicAddress));
    };

    const onCancel = () => {
        props.history.push("/");
    };
    const onLogin = () => {
        props.history.push("/login");
    };

    return (
        <ThemeProvider theme={Theme}>
            <div className="Landing">
                    <Container>
                        <center>
                            <br/>
                            <Col>
                                <SignupComponent
                                    user={userData.user}
                                    onLogin={onLogin}
                                    onCancel={onCancel}
                                    onSignup={onSignUp}
                                />
                            </Col>
                            <br/>
                        </center>
                    </Container>
            </div>
        </ThemeProvider>
    );
}

export default connect()(withRouter(SignupView));
