import React, { Component } from "react";
import CloseIcon from "@material-ui/icons/Close";
import ConfirmationPopup from "./ConfirmationPopup";
class Popup extends React.Component {
    constructor() {
        super();
        this.state = {
            showConfirmationPopup: false,
            showPopup: true,
        };
    }
    toggleConfirmationPopup() {
        this.setState({
            showConfirmationPopup: !this.state.showConfirmationPopup,
        });
    }
    togglePopup() {
        this.setState({
            showPopup: !this.state.showPopup,
        });
    }
    toggleBoth() {
        this.setState({
            showConfirmationPopup: !this.state.showConfirmationPopup,
            showPopup: !this.state.showPopup,
        });
    }
    render() {
        if (this.state.showPopup) {
            return (
                <div className="popup">
                    <div className="popup_inner">
                        <button id="close" onClick={this.props.closePopup}>
                            <CloseIcon></CloseIcon>{" "}
                        </button>

                        <h3
                            id="universities"
                            onClick={this.toggleBoth.bind(this)}
                        >
                            Technical University of Munich{" "}
                        </h3>
                        <h3
                            id="universities"
                            onClick={this.toggleBoth.bind(this)}
                        >
                            Ludwig Maximilian University of Munich
                        </h3>
                        <h3
                            id="universities"
                            onClick={this.toggleBoth.bind(this)}
                        >
                            {" "}
                            ETH ZÃ¼rich{" "}
                        </h3>
                        <h3
                            id="universities"
                            onClick={this.toggleBoth.bind(this)}
                        >
                            {" "}
                            University of Twente
                        </h3>
                        <div>
                            {" "}
                            {this.state.showConfirmationPopup ? (
                                <ConfirmationPopup
                                    text="Close Me"
                                    closePopup={this.toggleConfirmationPopup.bind(
                                        this
                                    )}
                                />
                            ) : null}
                        </div>
                    </div>
                </div>
            );
        } else
            return (
                <div>
                    {" "}
                    {this.state.showConfirmationPopup ? (
                        <ConfirmationPopup
                            text="Close Me"
                            closePopup={this.toggleConfirmationPopup.bind(this)}
                        />
                    ) : null}
                </div>
            );
    }
}
export default Popup;
