import UploadFileIcon from "@mui/icons-material/UploadFile";
import "../App.css";
import Grid from "@material-ui/core/Grid";
import { Button } from "@mui/material";
import React, { Component } from "react";
import Popup from "../component/Popup.js";
import AppNavBar from "../component/AppNavBar";
import AlertDialog from "../component/AlertDialog";
import FolderIcon from "../component/FolderIcon";

class Upload extends React.Component {
    constructor() {
        super();
        this.state = {
            showPopup: false,
            showSendToUniversity: false,
            uploadfile:{},
        };
        this.handleselectedFile = this.handleselectedFile.bind(this);

    }
    togglePopup() {
        this.setState({
            showPopup: !this.state.showPopup,
        });
    }

    handleselectedFile =(event) => {
        var file = event.target.files[0];
        var reader = new FileReader();
        let content= {};
         reader.onload = (event) => {

             content = event.target.result;
            console.log(content)
        };
        (reader.onload)();
        reader.readAsText(file);
         this.setState({
            selectedFile: event.target.files[0],
            selectedFileName: event.target.files[0].name,
            showSendToUniversity: true,
            uploadfile:content,
        });
        console.log();

    }
    async componentDidUpdate() { 
        console.log(this.state.uploadfile);

    }
    render() {
        return (
            <container>
                <AppNavBar />
                <Grid id="iconsPosition" container>
                    <Grid xs={1}> </Grid>
                    <div id="UploadButton">
                        <input
                            type="file"
                            name="file"
                            accept=".pdf,.json,.doc,.docx"
                            id="file-upload"
                            onChange={this.handleselectedFile}
                            style={{ display: "none" }}
                        />
                        <label htmlFor="file-upload">
                            <Button
                                variant="contained"
                                color="primary"
                                component="span"
                                endIcon={<UploadFileIcon />}
                            >
                                Upload Your File
                            </Button>
                        </label>

                        <p>{this.state.selectedFileName}</p>
                    </div>
                    <div>
                        {" "}
                        {this.state.showSendToUniversity ? (
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={this.togglePopup.bind(this)}
                                id="SendToUniversity"
                            >
                                Send to University{" "}
                            </Button>
                        ) : null}
                    </div>

                    <div>
                        {" "}
                        {this.state.showPopup ? (
                            <Popup
                                text="Close Me"
                                closePopup={this.togglePopup.bind(this)}
                            />
                        ) : null}
                    </div>
                </Grid>
            </container>
        );
    }
}
export default Upload;
