import axios from 'axios';
import {
  SIGNUP_SUCCESS,
  SIGNUP_FAIL,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  LOGOUT,
  USER_LOADED,
  AUTH_ERROR,
} from './types';
import { setAlert } from './alert';
import setAuthToken from '../utils/setAuthToken';

export const loadUser = () => async (dispatch) => {
  if (localStorage.token) {
    setAuthToken(localStorage.token);
  }


};

export const signup =
  ({
    firstName,
    lastName,
    phone,
    address,
    email,
    password,
    password2,
    dateOfBirth,
    paypalEmail
  }) =>
  async (dispatch) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    const body = JSON.stringify({
      firstName,
      lastName,
      phone,
      address,
      email,
      password,
      password2,
      dateOfBirth,
      paypalEmail
    });

  };

export const login = (email, password) => async (dispatch) => {
  const config = {
    headers: {
      'Content-Type': 'application/json',
    },
  };


};

export const logout = () => (dispatch) => {
  dispatch({
    type: LOGOUT,
  });
};
