import UploadFileIcon from "@mui/icons-material/UploadFile";
import "../App.css";
import Grid from "@material-ui/core/Grid";
import { Button } from "@mui/material";
import React, { Component } from "react";
import Popup from "../component/Popup.js";
import AppNavBar from "../component/AppNavBar";
import AlertDialog from "../component/AlertDialog";
import FolderIcon from "../component/FolderIcon";
import SelectInput from "@mui/material/Select/SelectInput";
import axios from 'axios';


class Upload extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showPopup: false,
            showSendToUniversity: false,
            uploadfile:{},
            testBackend:"",
        };
        this.handleselectedFile = this.handleselectedFile.bind(this);
    }
    togglePopup() {

        this.setState({
            showPopup: !this.state.showPopup,
        });
    }


     handleselectedFile = async (event) => {
        var file = event.target.files[0];
        var reader = new FileReader();
         reader.onload = (event)=>{
            this.setState({
                uploadfile:event.target.result, 
            })
         }

        reader.readAsText(file);
         this.setState({
            selectedFile: event.target.files[0],
            selectedFileName: event.target.files[0].name,
            showSendToUniversity: true,

        });
        await axios.get('http://localhost:4005/user/619e4acf6c9cd89c99017c92' ).then((response) => {
            this.setState({
                testBackend:response.data.username,
            });
            console.log(response.data.username);
          });

    }
    sleep(ms) {
        return new Promise((resolve) => setTimeout(resolve, ms));
      }

    render() {
        return (
            <container>
                <AppNavBar />
                <Grid id="iconsPosition" container>
                    <Grid xs={1}> </Grid>
                    <div id="UploadButton">
                        <input
                            type="file"
                            name="file"
                            accept=".pdf,.json,.doc,.docx"
                            id="file-upload"
                            onInput={this.handleselectedFile}
                            style={{ display: "none" }}
                        />
                        <label htmlFor="file-upload">
                            <Button
                                variant="contained"

                                component="span"
                                endIcon={<UploadFileIcon />}
                                style={{
                                    backgroundColor: "#3f50b5",
                                }}
                            >
                                Upload Your File
                            </Button>
                        </label>

                        <p>{this.state.selectedFileName}</p>
                    </div>
                    <div>
                        {" "}
                        {this.state.showSendToUniversity ? (
                            <Button
                            style={{
                                backgroundColor: "#3f50b5",
                            }}
                                variant="contained"
                                onClick={this.togglePopup.bind(this)}
                                id="SendToUniversity"
                            >
                                Send to University{" "}
                            </Button>
                        ) : null}
                    </div>

                    <div>
                        {" "}
                        {this.state.showPopup ? (
                            <Popup
                                text="Close Me"
                                closePopup={this.togglePopup.bind(this)}
                                fileInfo={this.state.uploadfile}
                            />
                        ) : null}
                    </div>
                </Grid>
            </container>
        );
    }
}
export default Upload;
