import UploadFileIcon from '@mui/icons-material/UploadFile';
import './App.css';
import Grid from '@material-ui/core/Grid';
import { Checkbox } from '@mui/material';
import { Button } from '@mui/material';
import React, { Component } from 'react';
import Popup from './component/Popup.js';
import FolderIcon from './component/FolderIcon';



class Upload extends React.Component {
    constructor() {
        super();
        this.state = {
          showPopup: false,
          showSendToUniversity:false
        };
        this.handleselectedFile = this.handleselectedFile.bind(this);

      }
      togglePopup() {
        this.setState({
          showPopup: !this.state.showPopup
        });
      }

      handleUpload (event){
      console.log(event);
      }

      handleselectedFile (event)  {
        console.log(event.target.files[0].name);
        this.setState({
            selectedFile: event.target.files[0],
            //**** added line below ****//

            selectedFileName: event.target.files[0].name,
            showSendToUniversity:true,
        });
    };
    render() {

  return (  
     <container>
    <Grid id="iconsPosition" container>
    <Grid xs={1}>  </Grid>

     <input
    type="file"
    name="file"
    id="file-upload"
    onChange={this.handleselectedFile}
    style={{ display: "none" }}
/>
<label  htmlFor="file-upload">
    <Button
        variant="contained"
        color="primary"
        component="span"
        endIcon={<UploadFileIcon />}
    >Upload Your File 
    </Button>
</label>

<p>{this.state.selectedFileName}</p>
<div> {this.state.showSendToUniversity ? 
        <Button onClick={this.togglePopup.bind(this)} id="UploadButton" variant="outlined">Send it to University </Button>
        : null
      }
      </div>);

   <div> {this.state.showPopup ? 
          <Popup
            text='Close Me'
            closePopup={this.togglePopup.bind(this)}
          />
          : null
        }
        </div>
    </Grid>

    </container>

  );
}
}
export default Upload;
