import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Card, CardContent, CardHeader, cardFooter, Button, TextField, Typography } from "@material-ui/core";
import AlertDialog from "../modal/Dialog";
import Alert from '@material-ui/lab/Alert';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import AccountCircle from '@material-ui/icons/AccountCircle';
import Input from '@material-ui/core/Input';
import FormControl from '@material-ui/core/FormControl';
import { useSelector } from "react-redux";

const useStyles = makeStyles((theme) => ({
    userLoginRoot: {
        margin: "auto",
    },
    loginPaper: {
        width: "400px",
        padding: theme.spacing(2),
    },
    margin: {
        margin: theme.spacing(1),
        display: 'flex',
        flexWrap: 'wrap',
    },
    loginRow: {
        paddingTop: theme.spacing(1),
        paddingBottom: theme.spacing(1),
        "&:last-child": {
            paddingBottom: theme.spacing(1),
        },
        "&:first-child": {
            paddingTop: theme.spacing(0),
        },
    },
    loginButtons: {
        display: "flex",
        justifyContent: "space-between",
    },
    loginButton: {
        marginLeft: theme.spacing(1),
    },
}));

/**
 * For user login
 * @param {props} props
 */
function LoginComponent(props) {
    const classes = useStyles();

    const user = useSelector((state) => state.user);

    const [username, setUsername] = React.useState("");
    const [loginError, setLoginError] = React.useState("");

    useEffect(() => {
        if (user?.error) {
            setLoginError(user?.error);
        } else {
        }
    }, [user?.error]);//, [user]);

    const onLogin = (e) => {
        e.preventDefault();
        props.onLogin(username);
    };


    const onCancel = (e) => {
        e.preventDefault();
        setLoginError("");
        setUsername("");
    };

    const onChangeUsername = (e) => {
        setUsername(e.target.value);
        setLoginError("");
    };

    const showError = () => (
        <div style={{ display: loginError ? '' : 'none' }}>
            <Alert variant="filled" severity="warning">{loginError}</Alert>
        </div>
    );

    return (
        <div className={classes.userLoginRoot}>
            <Card className={classes.loginPaper} component="form">
                <CardContent>
                    <form>
                        <FormControl className={classes.margin}>
                            <InputLabel htmlFor="input-with-icon-adornment">Username</InputLabel>
                            <Input
                                onChange={onChangeUsername}
                                value={username}
                                id="Username"
                                startAdornment={
                                    <InputAdornment position="start">
                                        <AccountCircle />
                                    </InputAdornment>
                                }
                            />
                        </FormControl>
                        <div className={classes.loginRow + " " + classes.loginButtons}>
                            <Button
                                className={classes.loginButton}
                                onClick={onCancel}
                            >
                                Cancel
                            </Button>
                            <Button
                                className={classes.loginButton}
                                variant="contained"
                                color="primary"
                                onClick={onLogin}
                                disabled={username === ""}
                                type="submit"
                            >
                                Login
                            </Button>

                        </div>
                    </form>
                </CardContent>
                <cardFooter>{showError()}</cardFooter>
            </Card>
        </div>
    );

}

export default LoginComponent;
