import UploadFileIcon from '@mui/icons-material/UploadFile';
import './App.css';
import Grid from '@material-ui/core/Grid';
import { Checkbox } from '@mui/material';
import { Button } from '@mui/material';
import React, { Component } from 'react';
import Popup from './component/Popup.js';
import FolderIcon from './component/FolderIcon';
import { Navbar, Nav, NavItem, NavDropdown } from 'react-bootstrap';
import Container from 'react-bootstrap/Container';
import { MenuItem } from 'react-bootstrap';


class Upload extends React.Component {
    constructor() {
        super();
        this.state = {
          showPopup: false,
          showSendToUniversity:false
        };
        this.handleselectedFile = this.handleselectedFile.bind(this);

      }
      togglePopup() {
        this.setState({
          showPopup: !this.state.showPopup
        });
      }


      handleselectedFile (event)  {
        console.log(event.target.files[0].name);
        this.setState({
            selectedFile: event.target.files[0],
            //**** added line below ****//

            selectedFileName: event.target.files[0].name,
            showSendToUniversity:true,
        });
    };
    render() {

  return (  


     <container>
           <Navbar>
        <Navbar.Header>
          <Navbar.Brand>
          </Navbar.Brand>
        </Navbar.Header>
        <Nav>
          <NavItem eventKey={1} href="#">Link</NavItem>
          <NavItem eventKey={2} href="#">Link</NavItem>
          <NavDropdown eventKey={3} title="Dropdown" id="basic-nav-    dropdown">
            <MenuItem eventKey={3.1}>Action</MenuItem>
            <MenuItem eventKey={3.2}>Another action</MenuItem>
            <MenuItem eventKey={3.3}>Something else here</MenuItem>
            <MenuItem divider />
            <MenuItem eventKey={3.4}>Separated link</MenuItem>
          </NavDropdown>
        </Nav>
      </Navbar>

    <Grid id="iconsPosition" container>
    <Grid xs={1}>  </Grid>
     <div id="UploadButton">
     <input
    type="file"
    name="file"
    id="file-upload"
    onChange={this.handleselectedFile}
    style={{ display: "none" }}
/>
<label  htmlFor="file-upload">
    <Button
        variant="contained"
        color="primary"
        component="span"
        endIcon={<UploadFileIcon />}
    >Upload Your File 
    </Button>
</label>

<p>{this.state.selectedFileName}</p>
</div>
<div> {this.state.showSendToUniversity ? 
        <Button variant="contained" color="primary" onClick={this.togglePopup.bind(this)} id="SendToUniversity" >Send it to University </Button>
        : null
      }
      </div>

   <div> {this.state.showPopup ? 
          <Popup
            text='Close Me'
            closePopup={this.togglePopup.bind(this)}
          />
          : null
        }
        </div>
    </Grid>

    </container>

  );
}
}
export default Upload;
